(1182, 67, '_form', '<div class="form-group col-xs-6">\n<label for="contact_name">Contact Name</label>\n[text* contact_name id:contact_name class:form-control class:gv-form-control class:gv-form-padding placeholder "Please enter your full name"]\n</div>\n\n<div class="form-group col-xs-6">\n<label for="company_name">Company Name</label>\n[text* company_name id:contact_name class:form-control class:gv-form-control class:gv-form-padding placeholder "Please enter your company name"]\n</div>\n\n<div class="form-group col-xs-6">\n<label for="client_name">Client Name</label>\n[text* client_name client_name class:form-control class:gv-form-control class:gv-form-padding placeholder "Please enter your client name"]\n</div>\n\n<div class="form-group col-xs-6">\n<label for="email_address">Email Address</label>\n[email* email_address id:email_address class:form-control class:gv-form-control class:gv-form-padding placeholder "Please enter your email address"]\n</div>\n\n<div class="form-group col-xs-6">\n<label for="contact_number">Contact number</label>\n[text* contact_number contact_number class:form-control class:gv-form-control class:gv-form-padding placeholder "Please enter a contact number"]\n</div>\n\n<hr class="hr-div col-xs-12">\n\n<div class="form-group col-xs-6">\n<label for="certificate">BASIX or Section J?</label>\n[select* certificate id:certificate class:form-control class:gv-form-control include_blank "BASIX" "Section J"]\n</div>\n\n<div class="form-group col-xs-6">\n<label for="date_required">Date Required</label>\n[date* date_required id:date_required class:gv-form-control]\n</div>\n\n<div class="form-group col-xs-6">\n<label for="plans_upload">Upload plans (max 5mb)</label>\n[file plans_upload id:plans_upload class:btn class:btn-default limit:5250000]\n</div>\n\n<div class="cf"></div>\n\n<div class="col-xs-12 quote-submit">\n[submit id:submit class:btn class:gv-btn class:gv-btn-quote class:col-xs-12 "Submit this quote"]\n</div>\n\n<div class="cf"></div>');
INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(1183, 67, '_mail', 'a:7:{s:7:"subject";s:13:"Website Quote";s:6:"sender";s:49:"[contact_name] - [company_name] <[email_address]>";s:4:"body";s:407:"[contact_name] from [company_name] has completed the quote form from the website.\n\n<strong>Contact Info<strong>\n\nContact Name - <strong>[contact_name]<strong>\nCompany Name - [company_name]\nClient Name - [client_name]\nEmail Address - [email_address]\nContact Number - [contact_number]\n\n<hr />\n\n<strong>Work Details<strong>\nCertificate - [certificate]\nDate Required - [date_required]\nPlans? - [plans_upload]\n--";s:9:"recipient";s:22:"petegorman@outlook.com";s:18:"additional_headers";s:0:"";s:11:"attachments";s:14:"[plans_upload]";s:8:"use_html";b:1;}'),
